import React, {useEffect, useRef, useState} from "react";
import {Card} from "../card";
import Box from "@material-ui/core/Box";
import {Message} from "../message";
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import {OptionalQuestionItem} from "../optional-question-item";
import List from "@material-ui/core/List";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControl from "@material-ui/core/FormControl";
// import {Intro} from "../intro";
import {OptionalQuestionAnswers} from "../optional-question-answers";
// import {OptionalQuestionAnswer} from "../optional-question-answer";
import {Answers} from "./answers";
import CustomButton from "../button";

const useStyles = makeStyles((theme) => ({
  section: {
    paddingTop: theme.spacing(4),
  },
  sectionBlock: {
    visibility: "hidden",
    height: 0,
    transition: "all 200ms linear",
    "& > *": {
      visibility: "hidden",
    },
  },
  button: {
    marginTop: theme.spacing(3),
  },
  answerTypography: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
  visible: {
    visibility: "visible",
    height: "auto",
    transition: "all 200ms linear",
    "& > *": {
      visibility: "visible",
    },
  }
}));

const userAvatar = "data:image/webp;base64,";
const systemMessageAvatar = "data:image/png;base64,";

export const Assignment1 = ({ sectionRef, targetRef, handleClick, children }) => {
  const classes = useStyles();

  const [value, setValue] = useState('');
  const [error, setError] = useState(false);
  const [isOpen, showAnswer] = useState(false);
  const [isTrue, setIsTrue] = useState(false);
  const [selectedOptionId, setSelectedOptionId] = useState(null);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isSubmitActive, setIsSubmitActive] = useState(false);
  const [isContinueActive, setIsContinueActive] = useState(true);
  const [isContinueBtn1Active, setIsContinueBtn1Active] = useState(true);
  const [isContinueBtn2Active, setIsContinueBtn2Active] = useState(false);

  const section1Ref = useRef();
  const section2Ref = useRef();
  const section1RefButton = useRef();
  const section2RefButton = useRef();
  const continueRefButton = useRef();
  const chooseOptionRefBtn = useRef();
  const option1ref = useRef();
  const option2ref = useRef();
  const option3ref = useRef();
  const option4ref = useRef();
  const option5ref = useRef();
  const option6ref = useRef();

  const handleInnerSectionScroll = (ref, buttonRef, alignType) => {
    ref.current.classList.add(classes.visible);
    setTimeout(() => {
      ref.current.scrollIntoView({
        behavior: 'smooth',
        block: alignType,
        inline: 'start',
      });
    }, 100)
    buttonRef.current.style.display = "none";
  }

  const handleRadioChange = (event) => {
    setValue(event.target.value);
    setSelectedOptionId(event.target.id);
    setError(false);
    setIsSubmitActive(true);
    console.log("handleRadioChange", event.target.id, typeof event.target.id);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (value !== "") {
      setError(false);
      setIsSubmitted(true);
      console.log("selectedOptionId === \"6\"", selectedOptionId === "6")
      selectedOptionId === "6" && setIsTrue(true);
      !error && setIsSubmitActive(false);
      !error && showAnswer(true);
    } else {
      setIsSubmitActive(false);
      setError(true);
    }
  };

  const handleContinue = (event) => {
    handleClick(targetRef);
    setIsContinueActive(false);
    event.target.style.display = "none";
  }
  useEffect(() => {
    sectionRef.current.scrollIntoView({
      behavior: 'smooth',
      block: 'start',
      inline: 'center',
    });
  }, [sectionRef]);

  useEffect(() => {
    console.log("isTrue index", isTrue);
  }, [isTrue]);

  useEffect(() => {
    window.addEventListener("keypress", (event) => {
      console.log("event.key", event.key)
      if (event.key === 'Enter') {
        console.log("enter pressed")
        console.log("section1Ref", section1Ref.current)
        console.log("section2Ref", section2Ref.current)
        console.log("section2RefButton", section2RefButton.current)

        if (isContinueBtn1Active) {
          console.log("isContinueBtn1Active clicked")
          section1RefButton.current.click();
          setIsContinueBtn1Active(false);
          setIsContinueBtn2Active(true);
        }

        if (isContinueBtn2Active) {
          console.log("isContinueBtn2Active clicked")
          section2RefButton.current.click();
          setIsContinueBtn2Active(false);
        }
      }
    })
  }, [isContinueBtn1Active, isContinueBtn2Active]);

  return (
    <Box ref={sectionRef} className={classes.section}>
      <Card count={1} countAmount={10} isCounted={true}>
        <Box>
          <Box>
            <Typography variant="body1">
              Its your first day at Educately Inc, the leading platform for courses and educational content. You came to the office a bit earlier to enjoy your customary cup of coffee.
            </Typography>
          </Box>
          <Box mt={2}>
            <Typography variant="body1">
              But no sooner does the coffee touch your lips than you suddenly get a message from Nathan, the CEO of Educately Inc.
            </Typography>
          </Box>
          <Box my={2}>
            <Typography variant="body1">
              Uh-oh, are you in trouble already?
            </Typography>
          </Box>
          <CustomButton
            ref={section1RefButton}
            type="submit"
            variant="contained"
            color="primary"
            size="large"
            onClick={() => handleInnerSectionScroll(section1Ref, section1RefButton, "center")}
            style={{marginTop: "20px"}}
          >Ok</CustomButton>
        </Box>
        <Box ref={section1Ref} className={`${classes.sectionBlock}`} style={{marginBottom: "-60px"}}>
          <Box style={{transition: "visibility 1s"}}>
            <Message avatar={systemMessageAvatar} text="Hey Frank, welcome aboard!" type="system" />
          </Box>
          <Box style={{transition: "visibility 2s"}}>
            <Message avatar={systemMessageAvatar} text="I know it's your first day, but could you do me a solid?" type="system" />
          </Box>
          <Box style={{transition: "visibility 3s"}}>
            <Message avatar={systemMessageAvatar} text="Take a look at our new financial course for women. It's been performing badly." type="system" />
          </Box>
          {/*<Box>*/}
          {/*  <Message avatar="" text="card" type="write" />*/}
          {/*</Box>*/}
          <Box>
            <Message avatar={userAvatar} text="Badly how? Can you be more specific pls?" type="default" />
          </Box>
          <Box>
            <Message avatar={systemMessageAvatar} text="Sorry, no can do. On a meeting right now. You'll figure it out." type="system" />
          </Box>
          {/*<Box>*/}
          {/*  <Message*/}
          {/*    avatar=""*/}
          {/*    text="confirm please"*/}
          {/*    type="system"*/}
          {/*    handleConfirm={() => handleClick(targetRef)}*/}
          {/*  />*/}
          {/*</Box>*/}
          <CustomButton
            ref={section2RefButton}
            type="submit"
            variant="contained"
            color="primary"
            size="large"
            onClick={() => handleInnerSectionScroll(section2Ref, section2RefButton, "start")}
            style={{marginTop: "32px"}}
          >Continue</CustomButton>
        </Box>
        <Box ref={section2Ref} className={`${classes.sectionBlock}`} style={{paddingTop: "60px"}}>
          <Box mt={2}>
            <Typography variant="body1">
              With a deep sigh, you sink back into your chair. The early bird gets the worm? Yeah, right.
            </Typography>
          </Box>
          <Box mt={2} mb={2}>
            <Typography variant="body1">
              You pull up the information on the course that Nathan mentioned, Wallet Detox. Which of its performance metrics are you going to look at?
            </Typography>
          </Box>
          <form onSubmit={handleSubmit}>
            <FormControl component="fieldset" error={error} style={{width: "100%"}}>
              <RadioGroup aria-label="quiz" name="quiz" value={value} onChange={handleRadioChange}>
                <List style={{ counterReset: "alphabeticList" }}>
                  <OptionalQuestionItem
                    ref={option1ref}
                    isTrue={false}
                    isOpen={isOpen}
                    value={value}
                    isSubmitted={isSubmitted}
                    id="1"
                    symbol="a"
                    // error={error}
                    text="NPS (Net Promoter Score)"
                  />
                  <OptionalQuestionItem
                    ref={option2ref}
                    isTrue={false}
                    isOpen={isOpen}
                    value={value}
                    isSubmitted={isSubmitted}
                    id="2"
                    symbol="b"
                    // error={error}
                    text="CSAT (Customer Satisfaction Index)"
                  />
                  <OptionalQuestionItem
                    ref={option3ref}
                    isTrue={false}
                    isOpen={isOpen}
                    value={value}
                    isSubmitted={isSubmitted}
                    id="3"
                    symbol="c"
                    // error={error}
                    text="Number of signups"
                  />
                  <OptionalQuestionItem
                    ref={option4ref}
                    isTrue={false}
                    isOpen={isOpen}
                    value={value}
                    isSubmitted={isSubmitted}
                    id="4"
                    symbol="d"
                    // error={error}
                    text="Revenue"
                  />
                  <OptionalQuestionItem
                    ref={option5ref}
                    isTrue={false}
                    isOpen={isOpen}
                    value={value}
                    isSubmitted={isSubmitted}
                    id="5"
                    symbol="e"
                    // error={error}
                    text="COR (Completion Rate)"
                  />
                  <OptionalQuestionItem
                    ref={option6ref}
                    isTrue={true}
                    isOpen={isOpen}
                    value={value}
                    isSubmitted={isSubmitted}
                    id="6"
                    symbol="f"
                    // error={error}
                    text="All of the above"
                  />
                </List>
              </RadioGroup>
            </FormControl>
            <OptionalQuestionAnswers isOpen={isOpen} isTrue={isTrue}>
              <Answers selectedOptionId={selectedOptionId} />
            </OptionalQuestionAnswers>
            {error && <div style={{ color: "red", marginBottom: "20px", marginTop: "-10px"}}>choose option</div>}
            {!isSubmitted && (
              <CustomButton
                ref={chooseOptionRefBtn}
                type="submit"
                variant="contained"
                color="primary"
                size="large"
                disabled={!isSubmitActive}
              >
                Check Answer
              </CustomButton>
            )}
          </form>
        </Box>
        {isSubmitted && isContinueActive && (<CustomButton
          ref={continueRefButton}
          type="button"
          variant="contained"
          color="primary"
          size="large"
          onClick={handleContinue}
          style={{marginTop: "8px"}}
        >
          Continue
        </CustomButton>)}
      </Card>
    </Box>
  )
};
